// Create the container element
const container = document.createElement("div");
container.classList.add("container");

// Create the Register heading
const heading = document.createElement("h2");
heading.textContent = "Register";

// Create the form element
const form = document.createElement("form");
form.id = "register-form";

// Create the username input field
const usernameLabel = document.createElement("label");
usernameLabel.textContent = "Username:";
const usernameInput = document.createElement("input");
usernameInput.type = "text";
usernameInput.id = "username";
usernameInput.placeholder = "Enter your username";
usernameInput.required = true;
const usernameFormGroup = document.createElement("div");
usernameFormGroup.classList.add("form-group");
usernameFormGroup.appendChild(usernameLabel);
usernameFormGroup.appendChild(usernameInput);

// Create the password input field
const passwordLabel = document.createElement("label");
passwordLabel.textContent = "Password:";
const passwordInput = document.createElement("input");
passwordInput.type = "password";
passwordInput.id = "password";
passwordInput.placeholder = "Enter your password";
passwordInput.required = true;
const passwordFormGroup = document.createElement("div");
passwordFormGroup.classList.add("form-group");
passwordFormGroup.appendChild(passwordLabel);
passwordFormGroup.appendChild(passwordInput);

// Create the confirm password input field
const confirmPasswordLabel = document.createElement("label");
confirmPasswordLabel.textContent = "Confirm Password:";
const confirmPasswordInput = document.createElement("input");
confirmPasswordInput.type = "password";
confirmPasswordInput.id = "confirm-password";
confirmPasswordInput.placeholder = "Confirm your password";
confirmPasswordInput.required = true;
const confirmPasswordFormGroup = document.createElement("div");
confirmPasswordFormGroup.classList.add("form-group");
confirmPasswordFormGroup.appendChild(confirmPasswordLabel);
confirmPasswordFormGroup.appendChild(confirmPasswordInput);

// Create the button group
const buttonGroup = document.createElement("div");
buttonGroup.classList.add("button-group");

// Create the Sign Up button
const signupButton = document.createElement("button");
signupButton.type = "submit";
signupButton.id = "signup-button";
signupButton.textContent = "Sign Up";

// Create the Back to Login button
const backButton = document.createElement("button");
backButton.type = "button";
backButton.id = "back-button";
backButton.textContent = "Back to Login";

// Append elements to the form
form.appendChild(usernameFormGroup);
form.appendChild(passwordFormGroup);
form.appendChild(confirmPasswordFormGroup);
form.appendChild(buttonGroup);
buttonGroup.appendChild(signupButton);
buttonGroup.appendChild(backButton);

// Append elements to the container
container.appendChild(heading);
container.appendChild(form);

// Append container to the document body
document.body.appendChild(container);

// CSS styles (register.css) can be applied as well

// Add event listeners
form.addEventListener("submit", function (event) {
  event.preventDefault();

  const username = usernameInput.value;
  const password = passwordInput.value;
  const confirmPassword = confirmPasswordInput.value;

  if (password !== confirmPassword) {
    alert("Passwords do not match.");
    return;
  }

  // Perform registration logic here
  console.log("Register:", username, password);

  // Clear form inputs
  usernameInput.value = "";
  passwordInput.value = "";
  confirmPasswordInput.value = "";
});

backButton.addEventListener("click", function () {
  // Redirect to the login page
  window.location.href = "login.html";
});
