import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./register.css";

function Register() {
  const navigate = useNavigate();
  const [inputs, setInputs] = useState({});
  const [username, setusername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showLabel, setshowLabel] = useState(false);
  const [backlogin, setbacklogin] = useState(true);
  const [begin, setbegin] = useState(true);
  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (password !== confirmPassword) {
      alert("Passwords don't match");
      setshowLabel(true);
      setshowLabel("Password and Confirm Password not match!!! ");
      // setusername('');
      setPassword("");
      setConfirmPassword("");

      return;
    }
    try {
      //Send a POST request to the server to create a new user
      const response = await axios.post("http://localhost:9000/user/signUp", {
        userName: username,
        password: password,
        confirmedPassword: confirmPassword,
      });
      // Show success message and redirect to login page
      alert("Account successfully created!");
      navigate("/ClientPage");
      // setbacklogin(false);
    } catch (error) {
      setshowLabel(`${error.response.data.error}`);
      console.error(error);
      // alert(`${username},${password},${confirmPassword}`);
      alert("Failed to create account");
      return;
    }
  };
  return (
    <div className="Register">
      <header className="Register-header">
        <h1>Fuel Prediction App</h1>
      </header>
      <form onSubmit={handleSubmit} className="Form">
        <div>
          <input
            type="text"
            name="username"
            value={inputs.username || ""}
            onChange={handleChange}
            placeholder="Username"
            required
          />
        </div>
        <div>
          <input
            type="password"
            name="password"
            value={inputs.password || ""}
            onChange={handleChange}
            placeholder="Password"
            required
          />
        </div>
        <div>
          <input
            type="password"
            name="confirmPassword"
            value={inputs.confirmPassword || ""}
            onChange={handleChange}
            placeholder="Confirm Password"
            required
          />
        </div>
        <div>
          <button id="register" type="submit">
            Register
          </button>
        </div>
      </form>
      <div className="Login-link">
        <Link to="/login">Back to Login</Link>
      </div>
    </div>
  );
}

export default Register;
