import "./App.css";
import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect,
  useHistory,
} from "react-router-dom";
import HomePage from "./components/homePage";
import FuelFormHistory from "./components/fuelFormHistory";
import FuelForm from "./components/fuelForm";
import Navbar from "./components/navbar";
import LoginForm from "./LoginForm";
import axios from "axios";
import Register from "./components/register";
import ClientProfilePage from "./components/clientProfile";

function App() {
  const adminUser = { name: "Harry Potter", password: "123456" };
  const [user, setUser] = useState({ name: "" });
  const [error, setError] = useState("");
  const history = useHistory();

  const Login = (details) => {
    const [serverUser, setServerUser] = useState();
    useEffect(() => {
      const fetchData = async () => {
        try {
          const response = await axios.post(
            "http://localhost:5001/api/user",
            details
          );
          setServerUser(response.data);
        } catch (error) {
          console.log(error);
        }
      };
      fetchData();
    }, []);

    console.log(details);
    if (
      //details.name == adminUser.name &&
      //details.password == adminUser.password
      serverUser == true
    ) {
      console.log("Logged in");
      setUser({
        name: details.name,
      });
    } else {
      console.log("Details do not match!");
      setError("Details do not match!");
    }
  };

  const Logout = () => {
    console.log("Logged out");
    setUser({ name: "" });
    history.push("/login");
  };

  return (
    <Router>
      <div>
        {user.name !== "" && <Navbar handleLogout={Logout} />}
        <Switch>
          <Route path="/" exact>
            {user.name !== "" ? <HomePage /> : <Redirect to="/login" />}
          </Route>
          {/* <Route path="/profile">
            {user.name !== "" ? (
              <>
                <Navbar handleLogout={Logout} />
                <ClientProfilePage />
              </>
            ) : (
              <Redirect to="/login" />
            )}
          </Route> */}
          <Route path="/fuelForm">
            {user.name !== "" ? (
              <>
                <Navbar handleLogout={Logout} />
                <FuelForm />
              </>
            ) : (
              <Redirect to="/login" />
            )}
          </Route>
          <Route path="/fuelFormHistory">
            {user.name !== "" ? (
              <>
                <Navbar handleLogout={Logout} />
                <FuelFormHistory />
              </>
            ) : (
              <Redirect to="/login" />
            )}
          </Route>
          <Route path="/login">
            {user.name !== "" ? (
              <Redirect to="/" />
            ) : (
              <LoginForm Login={Login} error={error} />
            )}
          </Route>
          <Route path="/register" component={Register} />
          <Redirect to="/login" />
        </Switch>
        {user.name !== "" && (
          <button onClick={Logout}>Logout</button> // Logout button
        )}
      </div>
    </Router>
  );
}

export default App;
